@RestController
public class TestController {
    private static final Logger log = Logger.getLogger(TestController.class);

    @Autowired
    TwilioSMSUtil twilioSMSUtil;

    @Autowired
    FCMNotificationUtil fcmNotificationUtil;

    @Autowired
    public JavaMailSender emailSender;

    @Autowired
    ImageUtil imageUtil;

    @Autowired
    GeneralUtil generalUtil;

    @Autowired
    AmazonUtil amazonUtil;

    @RequestMapping(value = { "/sms" }, method = RequestMethod.GET)
    public ResponseEntity<?> sendsms() {
        log.info("Sending sms");
        twilioSMSUtil.sendSMS("+971","0582865029","hello from bigorder");
        return ResponseEntity.ok(null);
    }

    @RequestMapping(value = { "/notification" }, method = RequestMethod.GET)
    public ResponseEntity<?> sendnotification() {
        log.info("Sending push notificaiton");
        Map<String, String> message = new HashMap<>();
        message.put("title", "test");
        message.put("body", "body message");
        PushNotification notification = new PushNotification();
        notification.setMessage(message);
        notification.setTokens(Collections.singletonList("e5Tl01vNwJU:APA91bElIfJjBQ53DbFBPh9E5HT7FipLlbH_GQs2mWiUPV7VMaKfl7U3wllbTV47fPy_5SqX4RUB1M4DoOrEVakUBwh-ize42tob1ScKnBdC2D6R1qr0_kRnHrX3Qgwm9jw-jljGhPVf"));

        fcmNotificationUtil.sendFCMPushNotification(notification);
        return ResponseEntity.ok(notification);
    }

    @RequestMapping(value = { "/email" }, method = RequestMethod.GET)
    public ResponseEntity<?> sendEmail() {
        log.info("Sending email");
        SimpleMailMessage message = new SimpleMailMessage();
        message.setTo("frank@yopmail.com");
        message.setSubject("New User");
        message.setText("I have something you Ms Rumi Subedi.");
        emailSender.send(message);
        return ResponseEntity.ok(message);
    }

    @RequestMapping(value = { "/email_attachment" }, method = RequestMethod.GET)
    public ResponseEntity<?> sendEmailAttachment() {
        log.info("Sending email with attachment");
        try {
            MimeMessage message = emailSender.createMimeMessage();
            MimeMessageHelper helper = new MimeMessageHelper(message,
                    MimeMessageHelper.MULTIPART_MODE_MIXED_RELATED,
                    StandardCharsets.UTF_8.name());

            helper.addAttachment("logo.png",  new URLDataSource(this.getClass().getResource("/static/logo1.png")));

            helper.setText("Test", true);
            helper.setSubject("test");
            helper.setTo("rumi@yopmail.com");

            emailSender.send(message);
            return ResponseEntity.ok(message);
        } catch (MessagingException e) {
            e.printStackTrace();
        }
        return ResponseEntity.ok(null);
    }

    @RequestMapping(value = "/upload", method = RequestMethod.GET)
    public ResponseEntity<?> uploadImage(){
        log.info("Uploading image");
        String dir = generalUtil.separateString("/", "Merchant_1", "Brand_12" );
        String s3PathLogo = "";
        String encodedImage="data:image/png;base64,";

        try{
            s3PathLogo = imageUtil.saveImageToBucket(encodedImage, "Encoded", dir, true);
        } catch (Exception e){}
        return ResponseEntity.ok(s3PathLogo);
    }

    @RequestMapping(value = "/upload_file", method = RequestMethod.GET)
    public ResponseEntity<?> uploadFileImage(){
        log.info("Uploading image");
        String dir = generalUtil.separateString("/", "Merchant_1", "Brand_12" );
        String s3PathLogo = "";
        File file = new File("C:\\Users\\Frank\\Desktop\\1.png");
        try{
            s3PathLogo = amazonUtil.uploadFileToS3(file, dir, "MyPic1.png", "MyPic.png");
        } catch (Exception e){}
        return ResponseEntity.ok(s3PathLogo);
    }
}
